function translate(dna) {
  var result = [];
  var translating = false;

  for (var i = 0; i < dna.length; i += (translating ? 3 : 1)) {
    var codon = dna.slice(i, i + 3);

    if (!translating) {
      if (codon === startCodon) {
        translating = true;
      } else {
        continue;
      }
    }

    var aminoAcid = CODONS[codon];

    if (aminoAcid === stopCodon) {
      break;
    }

    result.push(aminoAcid.key);
  }

  return result.join('');
}

function expressGene(dna) {
  var protein = translate(dna);
  return protein;
}

